import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';
import { string, any, number } from 'prop-types';
import ModalList from './ModalList';

class Customer extends React.Component<any, any> {
    private id: any
    constructor(props:any) {
        super(props);
        this.state = {
          data: [],
          setShow: false,
          isClose: true,
          NAME_OBJ: string,
          ID_OBJ: any,
          ID_OGR: number,
          NO_OBJ: any,
          CustomerCode: any,
          show: false
        };
        this.dataListCustomer= this.dataListCustomer.bind(this)
        this.handleDeleteData= this.handleDeleteData.bind(this)
      }
      componentDidMount() {
        fetch('http://api.o-nolimit.com/Admin/Api/Customer/LoadCustomersApi')
            .then(response => response.json())
            .then(data => this.setState({ data }))
            .catch(e => {
                console.log(e);
                return e;
            });
            // this.handleSubmit;
      }
       refreshPage(){ 
        setTimeout(
            function() {
                window.location.reload();
            },300
        );
 
    }
    showPopupEdit(){
        this.setState({show : true});
    }
       handleDeleteData(NO_OBJ: any) {     
        return fetch("http://api.o-nolimit.com/Admin/Api/Customer/DeleteCustomerApi?NO_OBJ="+ NO_OBJ, {
          method: 'delete'
        }).then(response =>
          response.json().then(json => {
            return json;
          })
          
        );
        
      }

        dataListCustomer() {
          return(
            this.state.data.map((item: any, i: any)=>( 
                    <tr key = {item.NO_OBJ} >
                        <th className="w-2" scope="row">{item.STT}</th>
                        <td className="w-20">{item.CustomerCode}</td>
                        <td className="w-20">{item.CustomerName}</td>
                        <td className="w-20">{item.Email}</td>
                        <td className="w-9">{item.Phone}</td>
                        <td className="w-20">{item.Address}</td>
                        <td className="w-10">  
                            <button 
                            className="margin-l-20 btn btn-primary" 
                                        type="button"  
                                        
                                        onClick={() => {this.handleDeleteData(item.NO_OBJ); this.refreshPage();}}
                                        >
                                           
                                <span className="glyphicon glyphicon-trash"></span>
                            </button>
                           &nbsp;&nbsp;
                            <a href="#myModalCustomer" role="button" className="btn btn-primary" data-toggle="modal" onClick={this.showPopupEdit}>
                            <span className="glyphicon glyphicon-pencil"></span>                 
                            </a>
                        </td>
                    </tr>

                    
            )
            )
          ) 
    }

    render(){
        const styleshow = {
            display: this.state.show ? "block" : "none"
        }
        console.log(this.state.data)
        return (
            <div className="wrapper">
           <Sidebar/>
            <div id="content">
                <Header/>
                <div  style={{padding: "20px"}}>
                <h2>Danh mục khách hàng</h2>
                <div className="line"></div>
                <table className="table table-bordered table-striped">
                    <thead>
                        <tr>
                        <th scope="col " className="w-2">STT</th>
                        <th scope="col " className="w-20">Mã KH</th>
                        <th scope="col "className="w-20">Tên Khách Hàng</th>
                        <th scope="col "className="w-20">Email</th>
                        <th scope="col "className="w-9">Điện thoại</th>
                        <th scope="col "className="w-20">Địa chỉ</th>
                        <th scope="col "className="w-10">Hiển thị</th>
                        <th scope="col "className="w-1"></th>
                        </tr>
                    </thead>
                    <tbody>
                         {this.dataListCustomer()}
                    </tbody>
                </table>      
            </div>
            {/* Show popup edit */}
            <div className="modal fade" id="exampleModal"  role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog modal-lg" role="document">
                    <div className="modal-content">
                    <div className="modal-header">
                        <h4 className="modal-title" style={{width:"95%"}}>Chi tiết đơn hàng</h4>
                        <button type="button" className="close" style={{width:"5%"}} data-dismiss="modal">&times;</button>
                    </div>
                    <div className="modal-body">
                        <div className = "row">
                            <div className ="col-sm-6">
                                <label ><b>Mã đơn hàng</b></label>
                                <input className="text-input" type="text" placeholder="Mã đơn hàng" 
                                />
                                <label ><b>Nhóm Khách Hàng</b></label>
                                <select className="form-control select-input" id="exampleFormControlSelect1">
                                    <option>1</option>
                                    <option>2</option>
                                </select>   
                                <label ><b>Ngày về</b></label><br/>
                                <input className="date-style date-input" type="date" name="bday"></input>
                            </div>
                            <div className ="col-sm-6">
                                <label ><b>Tên Khách Hàng</b></label>
                                <input className="text-input" type="text" placeholder="Tên Khách hàng" 
                                        // ref={(ref) => {this.firstName = ref}}
                                />
                                <label ><b>Ngày đặt hàng</b></label><br/>
                                <input className="date-style date-input" type="date" name="bday"></input>
                                <label ><b>Tình trạng</b></label><br/>
                                <select className="form-control select-input" id="exampleFormControlSelect1">
                                    <option>1</option>
                                    <option>2</option>
                                </select>
                            </div>
                        </div>
                    </div>
                    <div className="modal-footer">
                        <div className="row">
                            <div className = "col-sm-6">
