import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';
import { Link } from 'react-router-dom';

class ListEmployees extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: []
        };
      }

      componentDidMount() {
        fetch('http://www.o-nolimit.com/Admin/Api/Employee/GetEmployees')
            .then(response => response.json())
            .then(data => this.setState({ data }))
            .catch(e => {
                console.log(e);
                return e;
            });
            // this.handleSubmit;
      }
      refreshPage(){ 
        setTimeout(
            function() {
                window.location.reload();
            },300
        );
 
    }
       handleDeleteData(NO_OBJ: any) {     
        return fetch("http://www.o-nolimit.com/Admin/Api/Employee/DeleteEmployeeApi?NO_OBJ="+ NO_OBJ, {
          method: 'delete'
        }).then(response =>
          response.json().then(json => {
            return json;
          })
          
        );
        
      }

      dataListEmployees() {
        return(
          this.state.data.map((item: any, i: any)=>
                  <tr  key = {item.NO_OBJ} >
                      <th className="w-7">{item.EmployeeCode}</th>
                      <td className="w-15">{item.EmployeeName}</td>
                      <td className="w-18">{item.Address}</td>
                      <td className="w-15">{item.Email}</td>
                      <td className="w-5">{item.HomePhone}</td>
                      <td className="w-5">{item.OfficePhone}</td>
                      <th className="w-5" >{item.MobilePhone}</th>
                      <td className="w-5">{item.Fax}</td>
                      <td className="w-9">{item.Birthday}</td>
                      <td className="w-5">{item.Sex}</td>
                      <td className="w-10">  
                          <button 
                                onClick={() => {this.handleDeleteData(item.NO_OBJ); this.refreshPage();}}
                                className="margin-l-20 btn btn-primary" 
                                type="button"  
                                data-toggle="modal" 
                          >
                              <span className="glyphicon glyphicon-trash"></span>
                          </button>
                          <button className="margin-l-20 btn btn-primary" 
                                      data-toggle="modal" 
                                      data-target="#exampleModal">
                              <span className="glyphicon glyphicon-pencil"></span>              
                          </button>
                      </td>
                  </tr>
          )
        ) 
  }
    render(){
        return (
            <div className="wrapper">
           <Sidebar/>
            <div id="content">
                <Header/>
                <div  style={{padding: "20px"}}>
                <h2>Chức năng thao tác danh mục nhân viên</h2>
                <div className="row">
                    <div className="col-sm-4">
                        <div className="row">
                            <div className="col-sm-12">
                               
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-5"></div>
                    <div className="col-sm-3">
                        <div className="row">
                            <div className="col-sm-5">
                               
                            </div>
                            <div className="col-sm-7">
                                <Link to="add-employees"><button type="button" className="btn btn-success">Thêm mới</button></Link>
                                
                            </div>                            
                        </div>
                    </div>
                </div>
                <div className="line"></div>

                <h2>Danh mục nhân viên</h2>
                <table className="table table-bordered table-striped">
                    <thead>
                        <tr>
                        <th scope="col " className="w-7">Mã NV</th>
                        <th scope="col " className="w-15">Tên NV</th>
                        <th scope="col "className="w-18">Địa chỉ</th>
                        <th scope="col "className="w-15">Email</th>
                        <th scope="col "className="w-5">Home Phone</th>
                        <th scope="col "className="w-5">Office Phone</th>
                        <th scope="col "className="w-5">Mobile Phone</th>
                        <th scope="col "className="w-5">Fax</th>
                        <th scope="col "className="w-9">Ngày sinh</th>
                        <th scope="col "className="w-5">Sex</th>
                        <th scope="col "className="w-10">Hiển thị</th>
                        <th scope="col "className="w-1"></th>
                        </tr>
                    </thead>
                    <tbody>
                         {this.dataListEmployees()}
                    </tbody>
                </table>      
            </div>
            {/* Show popup edit */}
            <div className="modal fade" id="exampleModal"  role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog modal-lg" role="document">
                    <div className="modal-content">
                    <div className="modal-header">
                        <h4 className="modal-title" style={{width:"95%"}}>Chi tiết đơn hàng</h4>
                        <button type="button" className="close" style={{width:"5%"}} data-dismiss="modal">&times;</button>
                    </div>
                    <div className="modal-body">
                        <div className = "row">
                            <div className ="col-sm-6">
                                <label ><b>Mã đơn hàng</b></label>
                                <input className="text-input" type="text" placeholder="Mã đơn hàng" 
                                />
