import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import { any } from 'prop-types';
import queryString from 'query-string';
import axios from 'axios';
import './order.css';

class DetailOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
            order: {
                "OrderId": 0,
                "OrderCode": "",
                "UserId": 0,
                "OrderDate": "",
                "Note": "",
                "VISIBLE": true,
                "Status": ""
            },
            lstDetail: [
                {
                    "OrderId": 0,
                    "OrderDetailId": 0,
                    "Link": "",
                    "ProductCode": "",
                    "Color": "",
                    "Size": "",
                    "Quantity": any,
                    "OrderPrice": any,
                    "PurchasePrice": any,
                    "Rate": any,
                    "Note": ""
                },
            ],
          token: sessionStorage.getItem('Token')
        };
      }

      componentDidMount() {
        const values = queryString.parse(this.props.location.search);
        fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrderMaster?NO_TRA_MAS='+ values.OrderId,
        {headers:new Headers ({
            "Token": this.state.token,
        })}
        )
            .then(response => response.json())
            .then(order => this.setState({ order }))
            .catch(e => {
                console.log(e);
                return e;
            });

            fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrderDetail?NO_TRA_MAS='+ values.OrderId,
        {headers:new Headers ({
            "Token": this.state.token,
        })}
        )
            .then(response => response.json())
            .then(lstDetail => this.setState({ lstDetail }))
            .catch(e => {
                console.log(e);
                return e;
            });
      }

      changeHandlerDetail=(e: any)=>{ 
        let dataLstDetail = this.state.lstDetail;
        dataLstDetail[e.target.name] = e.target.value;
        this.setState(this.state.lstDetail);
      }
      changeHandler=(e: any)=>{ 
            let dataOrder = this.state.order;
            dataOrder[e.target.name] = e.target.value;
            this.setState(this.state.order);
      }

      pushTable(){
          var row = {
                        "OrderId": 0,
                        "OrderDetailId": 0,
                        "Link": "",
                        "ProductCode": "",
                        "Color": "",
                        "Size": "",
                        "Quantity": any,
                        "OrderPrice": any,
                        "PurchasePrice": any,
                        "Rate": any,
                        "Note": ""
          }
          this.state.lstDetail.push(row);
          this.setState(this.state.lstDetail);
      }


      submitHandler = (e: any) =>{
        //
        e.preventDefault();
        //console.log(this.state.data);
        const values = queryString.parse(this.props.location.search);
        const headers = {
            'Content-Type': 'application/json',
            'Token': this.state.token
          }
        if(values.OrderId=="0")
        {
            axios.post('http://api.o-nolimit.com/Admin/Api/Order/InsertOrder', this.state)
            .then(response =>{
              if(response.status==201)
              {
                  alert("Thêm mới đơn hàng thành công!");
              }
              else  
              {
                alert(response.statusText);
              }
            })
            .catch(error =>{
              alert(error);
            });
        }
        else    
        {
            axios.put('http://api.o-nolimit.com/Admin/Api/Order/UpdateOrder', this.state,
            {
                headers : headers
            }).then(response =>{
              if(response.status==200)
              {
                  alert("Chỉnh sửa thông tin đơn hàng thành công!");
                  window.location.href= window.location.href ;
              }
              else  
              {
                alert(response.statusText);
              }
            })
            .catch(error =>{
              alert(error);
            });
        }   
      }
    render(){
        const {
            OrderId,
            OrderCode,
            UserId ,
            OrderDate ,
            Note ,
            VISIBLE ,
            Status 
         } =  this.state.order;
        //  const {
        //     OrderDetailId,
        //     Link,
        //     ProductCode ,
        //     Color ,
        //     Size ,
        //     Quantity ,
        //     OrderPrice ,
        //     PurchasePrice,
        //     Rate,
        //  } =  this.state.lstDetail;
        return (
            <form onSubmit={this.submitHandler}>
                <div className="modal-body p-4" id="result">
                   
                         <div className="row">
                             <div className="col-sm-5"> 
                                 {/* <div className="form-group">
                                     <label >Mã đơn hàng:</label>
                                     <input type="text" className="form-control"  
                                        name="OrderCode" 
                                        value={OrderCode} 
                                        onChange={this.changeHandler} />
                                 </div>   */}
                                 <div className="form-group">
                                     <label >Ngày:</label>
                                     <input type="date" className="form-control"
                                        name="OrderDate" 
                                        value={OrderDate} 
                                        onChange={this.changeHandler}
                                    />
                                 </div>
                                 <div className="form-group">
                                     <label >Trạng thái:</label>
                                     <input type="text" className="form-control" 
                                        name="Status" 
                                        value={Status} 
                                        onChange={this.changeHandler}
                                     />
                                 </div>
                             </div>
                             <div className="col-sm-2"></div>
                             <div className="col-sm-5">
                                 <div className="form-group">
                                     <label >Ghi chú:</label>
                                     <textarea className="form-control" rows={10} id="comment" 
                                        name="Note" 
                                        value={Note} 
                                        onChange={this.changeHandler}
                                     ></textarea>
                                 </div>
                             </div>
                         </div>
                     
                     <div className="line"></div>
                     <table className="table table-bordered table-striped">
                         <thead>
                             <tr>
                             <th scope="col">Mã trạng thái</th>
                             <th scope="col">Màu</th>
                             <th scope="col">Size</th>
                             <th scope="col">Số lượng</th>
                             <th scope="col">Giá đơn hàng</th>
                             <th scope="col">Giá mua</th>
                             <th scope="col">Tỷ lệ</th>
                             <th scope="col">Ghi chú</th>
                             </tr>
                         </thead>
                         <tbody className="height-tbody" >
                            {/* {
                                 this.state.lstDetail.map((item: any, i: any)=>
                                 <tr  key = {i} id="styleInput">

                                     <td ><input
                                     defaultValue={item.ProductCode}
                                      name="ProductCode" 
                                      value={ProductCode} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>

                                     <td ><input
                                     defaultValue={item.Color}
                                      name="Color" 
                                      value={Color} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>

                                     <td ><input
                                      defaultValue={item.Size}
                                      name="Size" 
                                      value={Size} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>

                                     <td ><input
                                     defaultValue={item.Quantity}
                                      name="Quantity" 
                                      value={Quantity} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>

                                     <td ><input
                                     defaultValue={item.OrderPrice}
                                      name="OrderPrice" 
                                      value={OrderPrice} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>

                                     <td ><input
                                     defaultValue={item.PurchasePrice}
                                      name="PurchasePrice" 
                                      value={PurchasePrice} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>

                                     <td ><input
                                      defaultValue={item.Rate}
                                      name="Rate" 
                                      value={Rate} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>

                                     <td ><input
                                     defaultValue={item.Note}
                                      name="Note" 
                                      value={Note} 
                                      onChange={this.changeHandlerDetail}
                                     ></input></td>
                                 </tr>
                             )
                            } */}
                         </tbody>
                     </table>
                     <div className="line"></div>
                     <div className="row">
                         <div className="col-sm-1">
                         <button  onClick={() => {this.pushTable()}} type="button" className="btn btn-primary" data-dismiss="modal">+</button>
                         </div>
                         <div className="col-sm-11">
                             <button type="submit" className="btn btn-primary" data-dismiss="modal">Lưu</button>
                         </div>
                     </div>

                </div>
                {/* <div className="modal-footer">
                    <div className="row">
                         <div className="col-sm-1">
                         </div>
                         <div className="col-sm-11">
                             <button type="submit" className="btn btn-primary" data-dismiss="modal">Lưu</button>
                         </div>
                     </div>
                </div> */}
            </form>
        );
    }

}

export default DetailOrder;
