import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Login.css';

class Login extends React.Component<any, any> {
    constructor(props: any) {
        super(props);
    
        this.state = {
            data: [],
            UserName: '',
            Password: '',
            Token: ''
        };
      }
      changeHandler=(e: any)=>{
        this.setState({[e.target.name]: e.target.value})
      }
      submitHandler=()=>{
        fetch('http://www.o-nolimit.com/Admin/Api/Login/CheckLogin?username='+this.state.UserName+'&password='+ this.state.Password)
            .then(response => response.json())
            .then(data => this.setState({ data }))
            .catch(e => {
                console.log(e);
                return e;
            });
      }
      componentDidMount() {
        
            // this.handleSubmit;
      }
    render(){
        const {
            UserName,
            Password,
            Token,
        }= this.state
        console.log(this.state.data)
        return (
            <form className ="login-form" action="/action_page.php" method="get"  onSubmit={this.submitHandler}>
            <div className="imgcontainer">
                <div className="avatar"></div>
            </div>

            <div className="container">
                <label ><b>Username</b></label>
                <input type="text" placeholder="Enter Username"
                name="UserName" 
                required value={UserName}
                onChange={this.changeHandler}/>

                <label ><b>Password</b></label>
                <input type="password" placeholder="Enter Password" 
                name="Password" 
                required value={Password}
                onChange={this.changeHandler}/>
                    
                <button type="submit">Login</button>
                <label>
                <input type="checkbox"  name="remember"/> Remember me
                </label>
            </div>

            <div className="container">
                <button type="button" className="cancelbtn">
                <a href="/sign-up">Sign Up</a>
                </button>
                <span className="psw">Forgot <a href="#">password?</a></span>
            </div>
            </form>
            
           
        );
    }

}

export default Login;
