import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import { any } from 'prop-types';
import queryString from 'query-string';
import axios from 'axios';
import './order.css';
import { ColumnDirective, ColumnsDirective, GridComponent } from '@syncfusion/ej2-react-grids';
import { Edit, EditSettingsModel, Inject, Toolbar, ToolbarItems } from '@syncfusion/ej2-react-grids';

class DetailOrder extends React.Component<any, any> {
    public editOptions: EditSettingsModel = { allowEditing: true, allowAdding: true, allowDeleting: true, mode: 'Normal' };
    public toolbarOptions: ToolbarItems[] = ['Add', 'Edit', 'Delete', 'Update', 'Cancel'];
  
    constructor(props:any) {
        super(props);
        this.state = {
                lstDetail: [
                    {
                        OrderId: 0,
                        OrderDetailId: 0,
                        Link: "",
                        ProductCode: "",
                        Color: "",
                        Size: "",
                        Quantity: any,
                        OrderPrice: any,
                        PurchasePrice: any,
                        Rate: any,
                        Note: ""
                    },
                ],
                order: {
                    OrderId: 0,
                    OrderCode: "",
                    UserId: 0,
                    OrderDate: "",
                    Note: "",
                    VISIBLE: true,
                    Status: ""
                },
          token: sessionStorage.getItem('Token'),
          showEdit: false,
          userid: sessionStorage.getItem('UserId')
        };
        
      }
         
      componentDidMount() {
        const values = queryString.parse(this.props.location.search);
        fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrderMaster?NO_TRA_MAS='+ values.OrderId,
        {headers:new Headers ({
            "Token": this.state.token,
        })}
        )
            .then(response => response.json())
            .then(order => this.setState({order}))
            .catch(e => {
                console.log(e);
                return e;
            });

            fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrderDetail?NO_TRA_MAS='+ values.OrderId,
        {headers:new Headers ({
            "Token": this.state.token,
        })}
        )
            .then(response => response.json())
            .then(lstDetail => this.setState({lstDetail}))
            .catch(e => {
                console.log(e);
                return e;
            });
      }

      changeHandler=(e: any)=>{ 
        let dataOrder = this.state.data.order;
        dataOrder[e.target.name] = e.target.value;
        this.setState(this.state.order);
      }

      submitHandler = (e: any) =>{
        let dataOrder = this.state.order;
        dataOrder.UserId=this.state.userid;
        e.preventDefault();
        //console.log(this.state.data);
        const values = queryString.parse(this.props.location.search);
        const headers = {
            'Content-Type': 'application/json',
            'Token': this.state.token
          }
            axios.put('http://api.o-nolimit.com/Admin/Api/Order/UpdateOrder', this.state,
            {
                headers : headers
            }).then(response =>{
              if(response.status==200)
              {
                  alert("Chỉnh sửa thông tin đơn hàng thành công!");
                  window.location.href= window.location.href ;
              }
              else  
              {
                alert(response.statusText);
              }
            })
            .catch(error =>{
              alert(error);
            });

      }
    render(){
        const {
            OrderId,
            OrderCode,
            UserId ,
            OrderDate ,
            Note ,
            VISIBLE ,
            Status 
         } =  this.state.order;
        return (
            <div>
                <div className="modal-body p-4" id="result">
                   
                         <div className="row">
                             <div className="col-sm-5"> 
                                 <div className="form-group">
                                     <label >Mã đơn hàng:</label>
                                     <input type="text" className="form-control"  
                                        name="OrderCode" 
                                        required value={OrderCode} 
                                        onChange={this.changeHandler} />
                                 </div>  
                                 <div className="form-group">
                                     <label >Ngày:</label>
                                     <input type="date" className="form-control"
                                        name="OrderDate" 
                                        required value={OrderDate} 
                                        onChange={this.changeHandler}
                                    />
                                 </div>
                                 {/* <div className="form-group">
                                     <label >Trạng thái:</label>
                                     <input type="text" className="form-control" 
                                        name="Status" 
                                        required value={Status} 
                                        onChange={this.changeHandler}
                                     />
                                 </div> */}
                             </div>
                             <div className="col-sm-2"></div>
                             <div className="col-sm-5">
                                 <div className="form-group">
                                     <label >Ghi chú:</label>
                                     <textarea className="form-control" rows={10} id="comment" 
                                        name="Note" 
                                        required value={Note} 
                                        onChange={this.changeHandler}
                                     ></textarea>
                                 </div>
                             </div>
                         </div>
                     
                     <div className="line"></div>
                     <div id="styleInput">
                     <GridComponent dataSource={this.state.lstDetail} editSettings={this.editOptions} toolbar={this.toolbarOptions} height={265}>
                        <ColumnsDirective>
                            <ColumnDirective field='ProductCode' headerText='Mã trạng thái' width='100' isPrimaryKey={true}/>
                            <ColumnDirective field='Color' headerText='Màu' width='120'/>
                            <ColumnDirective field='Size' headerText='Size' width='120' format="C2" />
                            <ColumnDirective field='Quantity' headerText='Quantity'  width='120'/>
                            <ColumnDirective field='OrderPrice' headerText='OrderPrice' width='120'/>
                            <ColumnDirective field='PurchasePrice' headerText='PurchasePrice' width='120'/>
                            <ColumnDirective field='Rate' headerText='Rate' width='120'/>
                        </ColumnsDirective>
                        <Inject services={[Edit, Toolbar]} />
                        </GridComponent>
                     </div> 
                     <div className="line"></div>
                </div>
                <div className="modal-footer">
                    <button type="submit" className="btn btn-primary" onClick={(i)=>{this.submitHandler(i)}}>Lưu</button>
                </div>
            </div>
        );
    }

}

export default DetailOrder;
