import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import ModalList from './ModalList';
import { Button, Modal, ButtonToolbar } from 'react-bootstrap';
import Header from '../sidebar/header';
import { string, any, number } from 'prop-types';


var NAME_OBJ: any
class Customer extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: [],
          setShow: false,
          isClose: true,
          NAME_OBJ: string,
          ID_OBJ: any,
          ID_OGR: number
        };
        this.dataListCustomer= this.dataListCustomer.bind(this)
      }
      componentDidMount() {
        fetch('http://www.o-nolimit.com/Admin/Api/Customer/LoadCustomers')
            .then(response => response.json())
            .then(data => this.setState({ data }))
            .catch(e => {
                console.log(e);
                return e;
            });
            // this.handleSubmit;
      }

      handleSubmit(event:any){ 
        event.preventDefault();
        fetch('http://www.o-nolimit.com/Admin/Api/Customer/InsertCustomerApi', {
         method: 'post',
         headers: {'Content-Type':'application/json'},
         body:JSON.stringify(this.state.data)
         
        });
        console.log(JSON.stringify(this.state.data))
       };

      dataListCustomer() {
          return(
            this.state.data.map(function(item: any, i: any){
                return(
                    // <div className="row border-row" key = {i}>
                    // <div className="col-sm-1 border-column" >{item.ID_OBJ}</div>
                    // <div className="col-sm-1 border-column">{item.NAME_OBJ}</div>
                    // <div className="col-sm-2 border-column" >{item.ID_OGR}</div>
                    // <div className="col-sm-2 border-column" ></div>
                    // <div className="col-sm-1 border-column" ></div>
                    // <div className="col-sm-1 border-column" ></div>
                    // <div className="col-sm-2 border-column" ></div>
                    // <div className="col-sm-1 border-column" ></div>
                    // <div className="col-sm-1" ></div>
                    <tr key = {i}>
                        <th scope="row">{item.INT6}</th>
                        <td>{item.ID_OBJ}</td>
                        <td>{item.NAME_OBJ}</td>
                        <td>{item.ID_OGR}</td>
                        <td>{item.TEXT4}</td>
                        <td>{item.TEXT2}</td>
                        <td></td>
                        <td></td>
                    </tr>
            //   </div>
                )
            })
          ) 
    }

    render(){
        const styleshow = {
            display: this.state.setShow ? "block" : "none"
        }
        console.log(this.state.data)
        return (
            <div className="wrapper">
           <Sidebar/>
            <div id="content">
                <Header/>
                <div  style={{padding: "20px"}}>
                <h2>Chức năng thao tác danh mục khách hàng</h2>
                <div className="row">
                    <div className="col-sm-4">
                        <div className="row">
                            <div className="col-sm-4">
                                <strong>Tìm kiếm:</strong>
                            </div>
                            <div className="col-sm-8">
                                <input className="form-control" type="text" placeholder="Search" aria-label="Search"/>
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-3">
                        
                    </div>
                    <div className="col-sm-3">
                        <div className="row">
                                <div className="col-sm-6" style={{textAlign:"center"}}>
                                    <strong>Thêm mới:</strong>
                                </div>
                                <div className="col-sm-6">
                                    {/* <ModalList/> */}
                                    <button onClick = {()=>{
                                        this.setState({ setShow: !this.state.setShow })
                                    }} type="button" className="btn btn-success">Thêm</button> 
                                </div>                            
                        </div>
                    </div>
                    <div className="col-sm-2">
                        <div className="row">
                            <div className="col-sm-5">
                                <strong>Export:</strong>
                            </div>
                            <div className="col-sm-7">
                                <button type="button" className="btn btn-success">Export</button>
                            </div>                            
                        </div>
                    </div>
                </div>
                <div className="line"></div>

                <h2>Danh mục khách hàng</h2>
                <table className="table table-striped">
                    <thead>
                        <tr>
                        <th scope="col">STT</th>
                        <th scope="col">Mã KH</th>
                        <th scope="col">Tên Khách Hàng</th>
                        <th scope="col">Nhóm Khách Hàng</th>
                        <th scope="col">Email</th>
                        <th scope="col">Điện thoại</th>
                        <th scope="col">Địa chỉ</th>
                        <th scope="col">Hiển thị</th>
                        </tr>
                    </thead>
                    <tbody>
