import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import axios from 'axios';
import { any } from 'prop-types';

class AddOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
            data:{
                lstDetail: [
                    {
                        OrderId: 0,
                        OrderDetailId: 0,
                        Link: "",
                        ProductCode: "",
                        Color: "",
                        Size: "",
                        Quantity: any,
                        OrderPrice: any,
                        PurchasePrice: any,
                        Rate: any,
                        Note: ""
                    },
                ],
                order: {
                    OrderId: 0,
                    OrderCode: "",
                    UserId: 0,
                    OrderDate: "",
                    Note: "",
                    VISIBLE: true,
                    Status: ""
                },
                
            },
          
          token: sessionStorage.getItem('Token'),
          showEdit: false,
          userid: sessionStorage.getItem('UserId')
        };
      }
      changeHandler=(e: any)=>{ 
        let data = this.state.data;
        data[e.target.name] = e.target.value;
        this.setState(this.state.data);
        console.log("order")
      }
    //   changeHandler=(e: any)=>{ 
    //     let dataLstDetail = this.state.data.lstDetail;
    //     dataLstDetail[e.target.name] = e.target.value;
    //     this.setState(this.state.data.lstDetail);
    //     console.log("detail")
    //   }
      pushTable(){
          var row = {
                        "OrderId": any,
                        "OrderDetailId": any,
                        "Link": "",
                        "ProductCode": "",
                        "Color": "",
                        "Size": "",
                        "Quantity": any,
                        "OrderPrice": any,
                        "PurchasePrice": any,
                        "Rate": any,
                        "Note": ""
          }
          this.state.data.lstDetail.push(row);
          this.setState(this.state.data.lstDetail);
      }


      submitHandler = (e: any) =>{
        let dataOrder = this.state.data.order;
        dataOrder.UserId=this.state.userid;
        e.preventDefault()
        this.setState({})
        axios
        .post('http://api.o-nolimit.com/Admin/Api/Order/InsertOrder', this.state.data,
        { headers: { 'Content-Type': 'application/json' ,
                      "Token": this.state.token} }
        )
        .then(response =>{
            if(response.status==201)
            {
                alert("Thêm mới đơn hàng thành công!");
            }
            else  
            {
              alert(response.statusText);
            }
        })
        // .then(response =>{
        //     console.log(response + "ahihiihihihi")
        //   })
        .catch(error =>{
            alert(error);
        })
      }
 
      changes(){
        this.setState({showEdit: true});
      }
    render(){
        const {
            OrderId,
            OrderCode,
            UserId ,
            OrderDate ,
            Note ,
            VISIBLE ,
            Status ,OrderDetailId,
            Link,
            ProductCode ,
            Color ,
            Size ,
            Quantity ,
            OrderPrice ,
            PurchasePrice,
            Rate, 
         } =  this.state.data;
        //  const {
        //     OrderDetailId,
        //     Link,
        //     ProductCode ,
        //     Color ,
        //     Size ,
        //     Quantity ,
        //     OrderPrice ,
        //     PurchasePrice,
        //     Rate, 
        //  } =  this.state.data.lstDetail;
        return (
            <div className="modal" id="myModalAdd"  role="dialog" aria-hidden="true">
            <div className="modal-dialog modal-full" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h2 style={{width: "98%"}}>Thêm đơn hàng</h2>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close" style={{width: "2%"}}>
                            <span aria-hidden="true">×</span>
                        </button>
                    </div>
                    <form action="/action_page.php" onSubmit={this.submitHandler}>
                <div className="modal-body p-4" id="result">
                         <div className="row">
                             <div className="col-sm-5"> 
                                 <div className="form-group">
                                     <label >Mã đơn hàng:</label>
                                     <input type="text" className="form-control"  
                                        name="OrderCode" 
                                        required value={OrderCode} 
                                        onChange={this.changeHandler} />
                                 </div>  
                                 <div className="form-group">
                                     <label >Ngày:</label>
                                     <input type="date" className="form-control"
                                        name="OrderDate" 
                                        required value={OrderDate} 
                                        onChange={this.changeHandler}
                                    />
                                 </div>
                                 {/* <div className="form-group">
                                     <label >Trạng thái:</label>
                                     <input type="text" className="form-control" 
                                        name="Status" 
                                        required value={Status} 
                                        onChange={this.changeHandler}
                                     />
                                 </div> */}
                             </div>
                             <div className="col-sm-2"></div>
                             <div className="col-sm-5">
                                 <div className="form-group">
                                     <label >Ghi chú:</label>
                                     <textarea className="form-control" rows={10} id="comment" 
                                        name="Note" 
                                        required value={Note} 
                                        onChange={this.changeHandler}
                                     ></textarea>
                                 </div>
                             </div>
                         </div>
                     
                     <div className="line"></div>
                     <table className="table table-bordered table-striped">
                         <thead>
                             <tr>
                             <th scope="col">Link</th>
                             <th scope="col">Mã hàng</th>
                             <th scope="col">Màu</th>
                             <th scope="col">Size</th>
                             <th scope="col">Số lượng</th>
                             <th scope="col">Giá đơn hàng</th>
                             <th scope="col">Giá mua</th>
                             <th scope="col">Tỷ lệ</th>
                             </tr>
                         </thead>
                         <tbody className="height-tbody" >  
                                 <tr  id="styleInput">
                                     <td ><input
                                      name="Link" 
                                      required value={Link} 
                                      onChange={this.changeHandler}
                                     ></input></td>
                                     <td ><input
                                      name="ProductCode" 
                                      required value={ProductCode} 
                                      onChange={this.changeHandler}
                                     ></input></td>

                                     <td ><input
                                      required value={Color}
                                      name="Color" 
                                      onChange={this.changeHandler}
                                     ></input></td>

                                     <td ><input
                                      required value={Size}
                                      name="Size" 
                                      onChange={this.changeHandler}
                                     ></input></td>

                                     <td ><input
                                      required value={Quantity}
                                      name="Quantity" 
                                      onChange={this.changeHandler}
                                     ></input></td>

                                     <td ><input
                                      required value={OrderPrice}
                                      name="OrderPrice" 
                                      onChange={this.changeHandler}
                                     ></input></td>

                                     <td ><input
                                      required value={PurchasePrice}
                                      name="PurchasePrice" 
                                      onChange={this.changeHandler}
                                     ></input></td>

                                     <td ><input
                                      required value={Rate}
                                      name="Rate" 
                                      onChange={this.changeHandler}
                                     ></input></td>
                                 </tr>
                         </tbody>
                     </table>
                     <div className="line"></div>
                     <div className="row">
                         <div className="col-sm-1">
                         <button  onClick={() => {this.pushTable()}} type="button" className="btn btn-primary">+</button>
                         </div>
                         <div className="col-sm-11">
                         <button type="submit" className="btn btn-primary">Lưu</button>
                         </div>
                     </div>

                </div>
            </form>
                </div>
            </div>
        </div>
           
        );
    }


}

export default AddOrder;
