import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';
import { Link } from 'react-router-dom';
import { any } from 'prop-types';
import DetailOrder from './detailOrder';

class Order extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: [],
          id: any,
          isShowDetail: false
        };
      }
      componentDidMount() {
        fetch('http://www.o-nolimit.com/Admin/Api/Order/GetOrders')
            .then(response => response.json())
            .then(data => this.setState({ data }))
            .catch(e => {
                return e;
            });
      }
    //   componentDidMount(){
    //       console.log("ssss:" + this.state.data);
    //   }
    detailOrder = () =>{
        this.setState({isShowDetail: true })
        return  this.state.isShowDetail? <DetailOrder/> : null
    }

      dataListOrder() {
        return(
          this.state.data.map((item: any, i: any)=>
             
                    <tr  key = {i} >
                      <th className="w-20" scope="row">{item.OrderCode}</th>
                      <td className="w-20">{item.OrderDate}</td>
                      <td className="w-30">{item.Note}</td>
                      <td className="w-20">{item.Status}</td>
                      <td className="w-10">  
                          <button 
                          className="margin-l-20 btn btn-primary" 
                                      type="button"  
                                      data-toggle="modal"
                                      data-target="#deletePopup"
                                      >
                              <span className="glyphicon glyphicon-trash"></span>
                          </button>
                         
                            <button className="margin-l-20 btn btn-primary" 
                                onClick ={()=>{this.detailOrder()}}
                            >  
                                <span className="glyphicon glyphicon-pencil"></span>              
                            </button>
                  
                      </td>
                  </tr>
          )
        ) 
  }
    render(){
        return (
            <div className="wrapper">
           <Sidebar/>
            <div id="content">
                <Header/>
                <DetailOrder/>
                <div  style={{padding: "20px", zIndex: 0}}>
                <h2>Chức năng thao tác danh mục nhân viên</h2>
                <div className="row">
                    <div className="col-sm-4">
                        <div className="row">
                            <div className="col-sm-12">
                               
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-5"></div>
                    <div className="col-sm-3">
                        <div className="row">
                            <div className="col-sm-5">
                               
                            </div>
                            <div className="col-sm-7">
                                <Link to="add-order"><button type="button" className="btn btn-success">Thêm mới</button></Link>
                                
                            </div>                            
                        </div>
                    </div>
                </div>
                <div className="line"></div>

                <h2>Danh mục nhân viên</h2>
                <table className="table table-bordered table-striped">
                    <thead>
                        <tr>
                        <th scope="col " className="w-20">Mã Đơn Hàng</th>
                        <th scope="col " className="w-20">Ngày</th>
                        <th scope="col "className="w-30">Ghi chú</th>
                        <th scope="col "className="w-20">Trang thái</th>
                        <th scope="col "className="w-10">Hiển thị</th>
                        <th scope="col "className="w-1"></th>
                        </tr>
                    </thead>
                    <tbody>
                         {this.dataListOrder()}
                    </tbody>
                </table>      
            </div>
            {/* popup delete */}
            <div className="modal fade" id="deletePopup" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
            <div className="modal-dialog" role="document">
                <div className="modal-content">
                <div className="modal-header">
                    <div style={{width:"95%"}}></div>
                    <button style={{width:"5%"}} type="button" className="close" data-dismiss="modal" aria-label="Close">
                    <span aria-hidden="true">&times;</span>
                    </button>
                </div>
                <div className="modal-body">
                    Bạn có chắc muốn xóa?
                </div>
                <div className="modal-footer">
                    <button 
                            type="button" 
                            className="btn btn-secondary" 
                            data-dismiss="modal">Xóa</button>
                    <button type="button" className="btn btn-primary" data-dismiss="modal">Hủy</button>
                </div>
                </div>
                </div>
                </div>
                </div>   
                <div className="footer">
                    <p>Footer</p>
                </div>    
        </div>
        );
    }

}

export default Order;
