import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';

class DetailOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: [],
          dataDetail: []
        };
      }

      componentDidMount() {
        fetch('http://www.o-nolimit.com/Admin/Api/Order/GetOrderMaster?NO_TRA_MAS=226')
            .then(response => response.json())
            .then(data => this.setState({ data }))
            .catch(e => {
                return e;
            });
            this.callDetail();
      }
      callDetail(){
        fetch('http://www.o-nolimit.com/Admin/Api/Order/GetOrderDetail?NO_TRA_MAS=226')
            .then(response => response.json())
            .then(dataDetail => this.setState({ dataDetail }))
            .catch(e => {
                return e;
            });
      }

      changeHandler=(e: any)=>{
        this.setState({[e.target.name]: e.target.value})
      }
      dataListDetail() {
        return(
          this.state.dataDetail.map(function(item: any, i: any){
              return( 
                  <tr  key = {i} >
                      <th  scope="row">{item.ProductCode}</th>
                      <td >{item.Color}</td>
                      <td >{item.Size}</td>
                      <td >{item.Quantity}</td>
                      <td >{item.OrderPrice}</td>
                      <td >{item.PurchasePrice}</td>
                      <td >{item.Rate}</td>
                      <td >{item.Note}</td>
                  </tr>
              )
          })
        ) 
  }
    render(){
       
        console.log(this.state.data)
        console.log(this.state.dataDetail)
        return (
            <div className="wrapper">
           <Sidebar/>

            <div id="content">
               <Header/>
               <div style={{padding: "20px"}}>
                    <h2>Chi tiết đơn hàng</h2>
                    <div className="line"></div>
                    <form>
                        <div className="row">
                            <div className="col-sm-5"> 
                                <div className="form-group">
                                    <label >Mã đơn hàng:</label>
                                    <input type="text" className="form-control"  value={this.state.data.OrderCode}/>
                                </div>  
                                <div className="form-group">
                                    <label >Ngày:</label>
                                    <input type="text" className="form-control" value={this.state.data.OrderDate}/>
                                </div>
                                <div className="form-group">
                                    <label >Trang thái:</label>
                                    <input type="text" className="form-control" value={this.state.data.Status}/>
                                </div>
                            </div>
                            <div className="col-sm-2"></div>
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >Ghi chú:</label>
                                    <textarea className="form-control" rows={10} id="comment" value={this.state.data.Note}></textarea>
                                </div>
                            </div>
                        </div>
                    </form>
                    <div className="line"></div>
                    <table className="table table-bordered table-striped">
                        <thead>
                            <tr>
                            <th scope="col">Mã trạng thái</th>
                            <th scope="col">Màu</th>
                            <th scope="col">Size</th>
                            <th scope="col">Số lượng</th>
                            <th scope="col">Giá đơn hàng</th>
                            <th scope="col">Giá mua</th>
                            <th scope="col">Tỷ lệ</th>
                            <th scope="col">Ghi chú</th>
                            </tr>
                        </thead>
                        <tbody className="height-tbody" >
                           {this.dataListDetail()}
                        </tbody>
                    </table>

                    <div className="row">
                        <div className="col-sm-10"></div>
                        <div className="col-sm-1">
                            <button type="submit" className="btn btn-primary">Hủy</button>
                        </div>
                        <div className="col-sm-1">
                            <button type="submit" className="btn btn-primary">Lưu</button>
                        </div>
                    </div>
                </div>
                <div className="footer">
                    <p>Footer</p>
                </div>
            </div>
        </div>
        );
    }

}

export default DetailOrder;
