import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Login.css';
import App from '../../App';

class Login extends React.Component<any, any> {
    constructor(props: any) {
        super(props);
    
        this.state = {
            data: [],
            UserName: '',
            Password: '',
            Token: '',
        };
         this.handleChangeUserName = this.handleChangeUserName.bind(this);
         this.handleChangePassWord = this.handleChangePassWord.bind(this);
         this.submitHandler = this.submitHandler.bind(this);
      }
      submitHandler(evt: any) {
        evt.preventDefault();
        fetch('http://www.o-nolimit.com/Admin/Api/Login/CheckLogin?username='+this.state.UserName+'&password='+ this.state.Password)
            .then(response => response.json())
            .then(data => this.setState({ data }))
            .catch(e => {
                console.log(e);
                return e;
            });
        sessionStorage.setItem('Token', (this.state.data.Token));
        sessionStorage.getItem('Token')
        sessionStorage.setItem('UserId', (this.state.data.UserId));
        sessionStorage.getItem('UserId')
        if(this.state.data.Token == sessionStorage.getItem('Token')){
            <App/>
        }

      }
      
      handleChangeUserName(event: any) {
        this.setState({
            UserName: event.target.value
        });
      }
      handleChangePassWord(event: any) {
        this.setState({
            Password: event.target.value
        });
      }

      submitHandlerCall=()=>{
        
            
      }
      componentDidMount() {
        
        
      }
     
    render(){
        // const {
        //     UserName,
        //     Password,
        //     Token,
        // }= this.state
        console.log(this.state.data)
        return (
            
            <form className ="login-form" onSubmit={this.submitHandler} >
            <div className="imgcontainer">
                <div className="avatar"></div>
            </div>

            <div className="container">
                <label ><b>Username</b></label>
                <input type="text" placeholder="Enter Username"
                   id="theInput" 
                   value={this.state.UserName} 
                   onChange={this.handleChangeUserName}
            />

                <label ><b>Password</b></label>
                <input type="password" placeholder="Enter Password" 
                  id="theInput" 
                  value={this.state.Password} 
                  onChange={this.handleChangePassWord}
      />
                    
                     {/* <input type="submit" /> */}
                <button type="submit" onClick ={()=>this.submitHandlerCall()}>Login</button>
                <label>
                <input type="checkbox"  name="remember"/> Remember me
                </label>
            </div>

            <div className="container">
                <button type="button" className="cancelbtn"
                >
                <a href="/sign-up">Sign Up</a>
                </button>
                <span className="psw">Forgot <a href="#">password?</a></span>
            </div>
            </form>
            
           
        );
    }

}

export default Login;
