import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './SignUp.css';
import axios from 'axios';

class SignUp extends React.Component<any, any> {
    constructor(props: any) {
        super(props);
        this.state = {
           //mail dki
           Email: '',
            //pass
            Password: '',
            //ten
            UserName: '',
            //ngay sinh
            DATE2: '',
            //sdt
            TEXT2: '',
            //dia chi
            TEXT4: '',
            //web
            TEXT7: '',
        };
    }
      changeHandler=(e: any)=>{
        this.setState({[e.target.name]: e.target.value})
      }
      submitHandler = (e: any) =>{
        e.preventDefault()
        console.log(this.state)
        axios
        .post('http://www.o-nolimit.com/Admin/Api/Register/SignUp', this.state)
        .then(response =>{
          console.log(response)
        })
        .catch(error =>{
          console.log(error)
        })
      }
    render(){
      const {
         //mail dki
         Email,
         //pass
         Password,
         //ten
         UserName,
         //ngay sinh
         Birthday,
         //sdt
         Phone,
         //dia chi
         TEXT4,
         //web
         TEXT7,
      } = this.state;
        return (
            <form action="/action_page.php" onSubmit={this.submitHandler}>
                <div className="container signin">
                    <h1>Register</h1>
                    <p>Please fill in this form to create an account.</p>
                    <hr/>

                    <label ><b>Mail đăng ký</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập mail đăng ký" 
                      name="Email" 
                      required value={Email}
                      onChange={this.changeHandler}
                    />

                    <label><b>Mật khẩu</b></label>
                    <input 
                      type="password" 
                      placeholder="Nhập mật khẩu" 
                      name="Password" 
                      required value={Password}
                      onChange={this.changeHandler}
                    />

                    {/* <label ><b>Xác nhận mật khẩu</b></label>
                    <input 
                      type="password" 
                      placeholder="Xác nhận mật khẩu" 
                      name="psw-repeat" required/> */}
                    <hr/>
                    <h3>
                    <b>Thông tin liên lạc</b>
                    </h3>
                    
                    <br/>
                    <label ><b>Họ và tên</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập họ và tên" 
                      name="UserName" 
                      required value={UserName} 
                      onChange={this.changeHandler}
                    />
                    <br/>
                    <label ><b>Ngày tháng năm sinh</b></label><br/>
                    <input 
                        className="date-style " 
                        type="date" 
                        name="Birthday" 
                        value={Birthday} 
                        onChange={this.changeHandler}>
                    </input>
                    <br/>
                    <label ><b>Số điện thoại</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập số điện thoại" 
                      name="Phone" 
                      required value={Phone} 
                      onChange={this.changeHandler}/>
                    <br/>
                    <label ><b>Địa chỉ</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập địa chỉ" 
                      name="TEXT4" 
                      required value={TEXT4}  
                      onChange={this.changeHandler}/>
                    <br/>
                    {/* <label ><b>Mail</b></label>
                    <input type="text" placeholder="Nhập mail" name="mail" required/> */}
                    <br/>
                    <label ><b>Facebook</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập tên Facebook"
                      name="TEXT7" 
                      required value={TEXT7}  
                      onChange={this.changeHandler}/>
                    <br/>
                    <label ><b>Mã kho khách</b></label><br/>
                    <label className="radio-inline"><input type="radio" name="optradio" checked/>Kho A</label>
                    <label className="radio-inline"><input type="radio" name="optradio"/>Kho B</label>
                    <label className="radio-inline"><input type="radio" name="optradio"/>Kho C</label>
                    <hr/>
                    <p>By creating an account you agree to our <a href="#">Terms & Privacy</a>.</p>

                    <button type="submit" className="registerbtn">Register</button>
                </div>
                
                <div className="container signin-text ">
                    <p>Already have an account? <a href="#">Sign in</a>.</p>
                </div>
            </form>
            
           
        );
    }

}

export default SignUp;
