import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';
import axios from 'axios';
import {
    BrowserRouter as Router,
    useParams,
    useLocation
  } from "react-router-dom";

class AddOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {

           //m√£ nv
           EmployeeCode: "",
           //ten nv
           EmployeeName: "",
           //dia chi
           Address: "" ,
           //mail
           Email: "",
           //
           HomePhone: "",
           //
           OfficePhone: "",
           //web
           MobilePhone: "",
            //mail
            Fax: "",
            //
            Birthday: "",
            //
            Sex: "",
            //
            UserName: "",
            //
            Password: ""
        };
      }

      submitHandler = (e: any) =>{
        e.preventDefault()
        console.log(this.state)
        axios
        .post('http://api.o-nolimit.com/Admin/Api/Employee/InsertEmployeeApi', this.state)
        .then(response =>{
          console.log(response)
        })
        .catch(error =>{
          console.log(error)
        })
      }
      changeHandler=(e: any)=>{
        this.setState({[e.target.name]: e.target.value})
      }
    //    useQuery() {
    //     return new URLSearchParams(useLocation().search);
    //     $()
    //   }
    hello()
    {
        return "abc";
    }
    render(){
        const {
            //m√£ nv
            EmployeeCode,
            //ten nv
            EmployeeName,
            //dia chi
            Address ,
            //mail
            Email,
            //
            HomePhone,
            //
            OfficePhone,
            //web
            MobilePhone,
             //mail
             Fax,
             //
             Birthday,
             //
             Sex,
             //
             UserName,
             //
             Password
         } = this.state;
        //  let { id } = useParams();
         console.log(this.hello());
        // let query = this.useQuery();
        // console.log(query.get("k"));
        return (
            <div className="wrapper">
           <Sidebar/>

            <div id="content">
               <Header/>
               <div style={{padding: "20px"}}>
                    <h2>Ch·ª©c nƒÉng thao t√°c th√™m m·ªõi ƒë∆°n h√†ng</h2>
                    <div className="line"></div>
                    <form action="/action_page.php" onSubmit={this.submitHandler}>
                        <div className="row">
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >M√£ ƒë∆°n h√†ng:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p m√£ ƒë∆°n h√†ng"
                                        name="EmployeeCode" 
                                        required value={EmployeeCode}
                                        onChange={this.changeHandler}
                                    />
                                </div>  
                                <div className="form-group">
                                    <label >Ng√†y t·∫°o ƒë∆°n h√†ng:</label>
                                    <input 
                                        style={{width: "100%"}}
                                        className="date-style " 
                                        type="date" 
                                        name="Birthday" 
                                        value={Birthday} 
                                        onChange={this.changeHandler}>
                                    </input>
                                </div>
                                <div className="form-group">
                                    <label >Tr·∫°ng th√°i:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p tr·∫°ng th√°i"
                                        name="Address" 
                                        required value={Address}
                                        onChange={this.changeHandler}
                                    />
                                </div>  
                                <div className="form-group">
                                    <label >Link:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p link"
                                        name="Email" 
                                        required value={Email}
                                        onChange={this.changeHandler}
                                    />
                                </div>
                                <div className="form-group">
                                    <label >M√£ tr·∫°ng th√°i:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p m√£ tr·∫°ng th√°i"
                                         name="HomePhone" 
                                         required value={HomePhone}
                                         onChange={this.changeHandler}
                                    />
                                </div>
                                <div className="form-group">
                                    <label >M√†u:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p M√†u"
                                        name="OfficePhone" 
                                        required value={OfficePhone}
                                        onChange={this.changeHandler}
                                    />
                                </div>  
                                <div className="form-group">
                                    <label >Size:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p size"
                                        name="MobilePhone" 
                                        required value={MobilePhone}
                                        onChange={this.changeHandler}
                                    />
                                </div>
                            </div>
                            <div className="col-sm-2"></div>
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >S·ªë l∆∞·ª£ng:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p s·ªë l∆∞·ª£ng"
                                        name="Fax" 
                                        required value={Fax}
                                        onChange={this.changeHandler}
                                    />
                                </div>  
        
                                <div className="form-group">
                                    <label >Gi√° ƒë∆°n h√†ng:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p gi√° ƒë∆°n h√†ng"
                                        name="Sex" 
                                        required value={Sex}
                                        onChange={this.changeHandler}
                                    />
                                </div>
                                <div className="form-group">
                                    <label >Gi√° mua:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p gi√° mua	"
                                        name="UserName" 
                                        required value={UserName}
                                        onChange={this.changeHandler}
                                    />
                                </div>
                                <div className="form-group">
                                    <label >T·ª∑ l·ªá:</label>
                                    <input type="text" className="form-control" placeholder="Nh·∫≠p t·ª∑ l·ªá"
                                        name="Password" 
                                        required value={Password}
                                        onChange={this.changeHandler}
                                    />
                                </div>
                                <div className="form-group">
                                    <label >Ghi ch√∫:</label>
                                    <textarea className="form-control" rows={10} id="comment" ></textarea>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-10"></div>
                            <div className="col-sm-1">
                                <button type="submit" className="btn btn-primary">H·ªßy</button>
                            </div>
                            <div className="col-sm-1">
                                <button type="submit" className="btn btn-primary">L∆∞u</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
            <div className="footer">
                <p>Footer</p>
            </div>
        </div>
        );
    }

}

export default AddOrder;
