import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';

class DetailOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: null
        };
      }

      componentDidMount() {
        // fetch('https://jsonplaceholder.typicode.com/todos/1')
        //     .then(response => response.json())
        //     .then(data => this.setState({ data }))
        //     .catch(e => {
        //         console.log(e);
        //         return e;
        //     });
      }

    render(){
        return (
            <div className="wrapper">
           <Sidebar/>

            <div id="content">
               <Header/>
               <div style={{padding: "20px"}}>
                    <h2>Chi tiết đơn hàng</h2>
                    <div className="line"></div>
                    <form>
                        <div className="row">
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >Mã đơn hàng:</label>
                                    <input type="text" className="form-control" placeholder="Nhập mã đơn hàng"/>
                                </div>  
                                <div className="form-group">
                                    <label >Ngày:</label>
                                    <input type="text" className="form-control" placeholder="Nhập ngày tạo đơn hàng"/>
                                </div>
                                <div className="form-group">
                                    <label >Trang thái:</label>
                                    <input type="text" className="form-control" placeholder="Nhập trạng thái"/>
                                </div>
                            </div>
                            <div className="col-sm-2"></div>
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >Ghi chú:</label>
                                    <textarea className="form-control" rows={10} id="comment"></textarea>
                                </div>
                            </div>
                        </div>
                    </form>
                    <div className="line"></div>
                    <table className="table table-striped">
                        <thead>
                            <tr>
                            <th scope="col">#</th>
                            <th scope="col">First</th>
                            <th scope="col">Last</th>
                            <th scope="col">Handle</th>
                            </tr>
                        </thead>
                        <tbody className="height-tbody" >
                            <tr>
                            <th scope="row">1</th>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>@mdo</td>
                            </tr>
                            <tr>
                            <th scope="row">2</th>
                            <td>Jacob</td>
                            <td>Thornton</td>
                            <td>@fat</td>
                            </tr>
                            <tr>
                            <th scope="row">3</th>
                            <td>Larry</td>
                            <td>the Bird</td>
                            <td>@twitter</td>
                            </tr>
                        </tbody>
                    </table>

                    <div className="row">
                        <div className="col-sm-10"></div>
                        <div className="col-sm-1">
                            <button type="submit" className="btn btn-primary">Hủy</button>
                        </div>
                        <div className="col-sm-1">
                            <button type="submit" className="btn btn-primary">Lưu</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        );
    }

}

export default DetailOrder;
