import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';

class AddEmployees extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: null
        };
      }

      componentDidMount() {
        // fetch('https://jsonplaceholder.typicode.com/todos/1')
        //     .then(response => response.json())
        //     .then(data => this.setState({ data }))
        //     .catch(e => {
        //         console.log(e);
        //         return e;
        //     });
      }

    render(){
        this.componentDidMount();
        return (
            <div className="wrapper">
           <Sidebar/>

            <div id="content">

               <Header/>
               <div style={{padding: "20px"}}>
               <h2>Chức năng thao tác thêm mới nhân viên</h2>
               <div className="line"></div>

                <h2>Danh mục khách hàng</h2>
                <div className="row border-row">
                    <div className="col border-column" >Mã nhân viên</div>
                    <div className="col border-column">Tên nhân viên</div>
                    <div className="col border-column" >Tên Khách Hàng</div>
                    <div className="col border-column" >Nhóm nhân viên</div>
                    <div className="col border-column" >ĐT văn phòng</div>
                    <div className="col border-column" >ĐT nhà riêng</div>
                    <div className="col border-column" >Di động</div>
                    <div className="col border-column" >Địa chỉ</div>
                    <div className="col border-column" >Số fax</div>
                    <div className="col border-column" >Email</div>
                    <div className="col border-column" >Hiển thị</div>
                    <div className="col" >Chi tiết</div>
                </div>
               </div>

                
            </div>
        </div>
        );
    }

}

export default AddEmployees;
