import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';
import { Link } from 'react-router-dom';
import { any } from 'prop-types';
import EditOrder from './editOrder';
import DetailOrder from './detailOrder';

class Order extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: [],
          id: any,
          isShowDetail: true,
          OrderId: any,
          token: sessionStorage.getItem('Token')
        };
        this.showDetail= this.showDetail.bind(this)
      }
      componentDidMount() {

        fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrders',
        {headers:new Headers ({
          "Token": this.state.token,
      })}
        ) 
            .then(response => response.json())
            .then(data => this.setState({data}))
            .catch(error =>{
              console.log("aaa" + error)
            })
            
      }
      showDetail(id: any){
        const styleshow = {
          display: this.state.isShowDetail ? "block" : "none"
      }
        this.setState({isShowDetail: true});
        this.setState({OrderId: id});
        return(
          <div style={styleshow}>
          <DetailOrder  onChange ={this.state.OrderId}/>
        </div>
        )

      }

      dataListOrder() {
          return(
            this.state.data.map((item: any, i: any)=>(
                      <tr  key = {item.OrderId} >
                        <th className="w-20" scope="row">{item.OrderCode}</th>
                        <td className="w-20">{item.OrderDate}</td>
                        <td className="w-30">{item.Note}</td>
                        <td className="w-20">{item.Status}</td>
                        <td className="w-10">  
                          <a href="#myModalAdd" role="button" className="btn btn-primary" data-toggle="modal" >
                              <span className="glyphicon glyphicon-pencil"></span>
                          </a>&nbsp;&nbsp;
                          <a href="#myModal" role="button" className="btn btn-primary" data-toggle="modal" 
                          onClick={() => {this.showDetail(item.OrderId)}}
                          >
                              <span className="glyphicon glyphicon-option-vertical"></span>              
                          </a>
                        </td>
                    </tr>
            )
            )
          )  
  }
    render(){
      console.log(this.state.data)
     
        return (
            <div className="wrapper">
           <Sidebar/>
            <div id="content">
                <Header/>
             
                <div  style={{padding: "20px", zIndex: 0}}>
                <h2>Danh mục đơn hàng</h2>
                <div className="row">
                    <div className="col-sm-4">
                        <div className="row">
                            <div className="col-sm-12">
                               
                            </div>
                        </div>
                    </div>
                    <div className="col-sm-5"></div>
                    <div className="col-sm-3">
                        <div className="row">
                            <div className="col-sm-5">
                               
                            </div>
                            <div className="col-sm-7">
                                <Link to="add-order"><button type="button" className="btn btn-success">Thêm mới</button></Link>
                            </div>                            
                        </div>
                    </div>
                </div>
                <div className="line"></div>
                <table className="table table-bordered table-striped">
                    <thead>
                        <tr>
                        <th scope="col " className="w-20">Mã Đơn Hàng</th>
                        <th scope="col " className="w-20">Ngày</th>
                        <th scope="col "className="w-30">Ghi chú</th>
                        <th scope="col "className="w-20">Trạng thái</th>
                        <th scope="col "className="w-10">Hiển thị</th>
                        <th scope="col "className="w-1"></th>
                        </tr>
                    </thead>
                    <tbody>
                         {this.dataListOrder()}
                    </tbody>
                </table>      
            </div>
           
                <div className="footer">
                    <p>Footer</p>
                </div>    
             

               <EditOrder/>
        </div>
        </div>
        );
    }

}

export default Order;
