    args.element.setAttribute('name', getComplexFieldID(args.column.field));
    this.obj.appendTo(args.element);
  };

  NumericEditCell.prototype.destroy = function () {
    if (this.obj && !this.obj.isDestroyed) {
      this.obj.destroy();
    }
  };

  return NumericEditCell;
