import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import axios from 'axios';
import { any } from 'prop-types';
import { ColumnDirective, ColumnsDirective, GridComponent } from '@syncfusion/ej2-react-grids';
import { Edit, EditSettingsModel, Inject, Toolbar, ToolbarItems } from '@syncfusion/ej2-react-grids';


class AddOrder extends React.Component<any, any> {
    public editOptions: EditSettingsModel = { allowEditing: true, allowAdding: true, allowDeleting: true, mode: 'Normal' };
    public toolbarOptions: ToolbarItems[] = ['Add', 'Edit', 'Delete', 'Update', 'Cancel'];
    constructor(props:any) {
        super(props);
        this.state = {
                lstDetail: [],
                order: {
                    OrderId: 0,
                    OrderCode: "",
                    UserId: 0,
                    OrderDate: "",
                    Note: "",
                    VISIBLE: true,
                    Status: ""                
            },
          
          token: sessionStorage.getItem('Token'),
          showEdit: false,
          userid: sessionStorage.getItem('UserId')
        };
      }
      changeHandler=(e: any)=>{ 
        let dataOrder = this.state.order;
        dataOrder[e.target.name] = e.target.value;
        this.setState(this.state.order);
        console.log("order")
      }
      refreshPage(){ 
        window.location.reload();
        }
      submitHandler = (e: any) =>{
        let dataOrder = this.state.order;
        dataOrder.UserId=this.state.userid;
        e.preventDefault()
        this.setState({})
        axios
        .post('http://api.o-nolimit.com/Admin/Api/Order/InsertOrder', this.state,
        { headers: { 'Content-Type': 'application/json' ,
                      "Token": this.state.token} }
        )
        .then(response =>{
            if(response.status==201)
            {
                alert("Thêm mới đơn hàng thành công!");
            }
            else  
            {
              alert(response.statusText);
            }
        })
        .catch(error =>{
            alert(error);
        })
      }
 
      changes(){
        this.setState({showEdit: true});
      }
    render(){
        const {
            OrderId,
            OrderCode,
            UserId ,
            OrderDate ,
            Note ,
            VISIBLE ,
            Status 
         } =  this.state.order;
        return (
            <div className="modal" id="myModalAdd"  role="dialog" aria-hidden="true">
            <div className="modal-dialog modal-full" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h2 style={{width: "98%"}}>Thêm đơn hàng</h2>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close" style={{width: "2%"}}
                        onClick={()=>{this.refreshPage()}}>
                            <span aria-hidden="true">×</span>
                        </button>
                    </div>
                    <form action="/action_page.php" onSubmit={this.submitHandler}>
                <div className="modal-body p-4" id="result">
                         <div className="row">
                             <div className="col-sm-5"> 
                                 <div className="form-group">
                                     <label >Mã đơn hàng:</label>
                                     <input type="text" className="form-control"  
                                        name="OrderCode" 
                                        required value={OrderCode} 
                                        onChange={this.changeHandler} />
                                 </div>  
                                 <div className="form-group">
                                     <label >Ngày:</label>
                                     <input type="date" className="form-control"
                                        name="OrderDate" 
                                        required value={OrderDate} 
                                        onChange={this.changeHandler}
                                    />
                                 </div>
                                 {/* <div className="form-group">
                                     <label >Trạng thái:</label>
                                     <input type="text" className="form-control" 
                                        name="Status" 
                                        required value={Status} 
                                        onChange={this.changeHandler}
                                     />
                                 </div> */}
                             </div>
                             <div className="col-sm-2"></div>
                             <div className="col-sm-5">
                                 <div className="form-group">
                                     <label >Ghi chú:</label>
                                     <textarea className="form-control" rows={10} id="comment" 
                                        name="Note" 
                                        required value={Note} 
                                        onChange={this.changeHandler}
                                     ></textarea>
                                 </div>
                             </div>
                         </div>
                     
                     <div className="line"></div>
                      <div id="styleInput">
                     <GridComponent dataSource={this.state.lstDetail} editSettings={this.editOptions} toolbar={this.toolbarOptions} height={265}>
                        <ColumnsDirective>
                            <ColumnDirective field='ProductCode' headerText='Mã trạng thái' width='120'/>
                            <ColumnDirective field='Color' headerText='Màu' width='120'/>
                            <ColumnDirective field='Size' headerText='Size' width='120' format="C2" />
                            <ColumnDirective field='Quantity' headerText='Quantity'  width='120'/>
                            <ColumnDirective field='OrderPrice' headerText='OrderPrice' width='120'/>
                            <ColumnDirective field='PurchasePrice' headerText='PurchasePrice' width='120'/>
                            <ColumnDirective field='Rate' headerText='Rate' width='120'/>
                        </ColumnsDirective>
                        <Inject services={[Edit, Toolbar]} />
                        </GridComponent>
                     </div>
                     <div className="line"></div>
                     <div className="modal-footer">
                        <button type="submit" className="btn btn-primary" onClick={(i)=>{this.submitHandler(i)}}>Lưu</button>
                    </div>
                </div>
            </form>
                </div>
            </div>
        </div>
           
        );
    }


}

export default AddOrder;
