import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './SignUp.css';
import axios from 'axios';

class SignUp extends React.Component<any, any> {
    constructor(props: any) {
        super(props);
        this.state = {
           //mail dki
           Email: '',
            //pass
            Password: '',
            //ten
            CustomerName : '',
            //ngay sinh
            Birthday: '',
            //sdt
            Phone: '',
            //dia chi
            Address: '',
            //web
            Facebook: '',
            isshow: false
        };
    }
      changeHandler=(e: any)=>{
        this.setState({[e.target.name]: e.target.value})
      }

      submitHandler = (e: any) =>{
        e.preventDefault()
        console.log(this.state)
        axios
        .post('http://api.o-nolimit.com/Admin/Api/Register/SignUp', this.state)
        .then(response =>{
          console.log(response)
          
        })
        .catch(error =>{
          console.log(error)
          alert("Tài khoản này đã tồn tại, vui lòng nhập tài khoản khác !")
        })
      }
    render(){
      const {
         //mail dki
         Email,
         //pass
         Password,
         //ten
         CustomerName ,
         //ngay sinh
         Birthday,
         //sdt
         Phone,
         //dia chi
         Address,
         //web
         Facebook,
      } = this.state;
        return (
        <div>
            <form action="/action_page.php" onSubmit={this.submitHandler}>
                <div className="container signin">
                    <h1>Register</h1>
                    <p>Please fill in this form to create an account.</p>
                    <hr/>

                    <label ><b>Mail đăng ký</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập mail đăng ký" 
                      name="Email" 
                      required value={Email}
                      onChange={this.changeHandler}
                    />

                    <label><b>Mật khẩu</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập mật khẩu" 
                      name="Password" 
                      required value={Password}
                      onChange={this.changeHandler}
                    />
                    <hr/>
                    <h3>
                    <b>Thông tin liên lạc</b>
                    </h3>
                    
                    <br/>
                    <label ><b>Họ và tên</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập họ và tên" 
                      name="CustomerName" 
                      required value={CustomerName} 
                      onChange={this.changeHandler}
                    />
                    <br/>
                    <label ><b>Ngày tháng năm sinh</b></label><br/>
                    <input 
                        className="date-style " 
                        type="date" 
                        name="Birthday" 
                        value={Birthday} 
                        onChange={this.changeHandler}>
                    </input>
                    <br/>
                    <label ><b>Số điện thoại</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập số điện thoại" 
                      name="Phone" 
                      required value={Phone} 
                      onChange={this.changeHandler}/>
                    <br/>
                    <label ><b>Địa chỉ</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập địa chỉ" 
                      name="Address" 
                      required value={Address}  
                      onChange={this.changeHandler}/>
                    <br/>
                    {/* <label ><b>Mail</b></label>
                    <input type="text" placeholder="Nhập mail" name="mail" required/> */}
                    <br/>
                    <label ><b>Facebook</b></label>
                    <input 
                      type="text" 
                      placeholder="Nhập tên Facebook"
                      name="Facebook" 
                      required value={Facebook}  
                      onChange={this.changeHandler}/>
                    <br/>
                    <label ><b>Mã kho khách</b></label><br/>
                    <label className="radio-inline"><input type="radio" name="optradio" checked/>Kho A</label>
                    <label className="radio-inline"><input type="radio" name="optradio"/>Kho B</label>
                    <label className="radio-inline"><input type="radio" name="optradio"/>Kho C</label>
                    <hr/>

                    <button type="submit" className="registerbtn">Register</button>
                </div>
            </form>
            <div className="modal fade" aria-labelledby="exampleModalLabel" aria-hidden="true">
              <div className="modal-dialog" role="document">
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                      <span aria-hidden="true">&times;</span>
                    </button>
                  </div>
                  <div className="modal-body">
                    ...
                  </div>
                  <div className="modal-footer">
                    <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                    <button type="button" className="btn btn-primary">Save changes</button>
                  </div>
                </div>
              </div>
            </div>
         </div>   
           
        );
    }

}

export default SignUp;
