import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import { any } from 'prop-types';

interface idProps {
    onChange?: any;
}
class DetailOrder extends React.Component<idProps, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: [],
          dataDetail: [],
          id: any,
          token: sessionStorage.getItem('Token')
        };
        this.callApi= this.callApi.bind(this)
        // this.callDetail= this.callDetail.bind(this)
      }
      componentWillMount() {
        
      }
      componentWillReceiveProps() {
        console.log(this.state.id)
        this.callDetail()

        // fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrder?NO_TRA_MAS=226',
        //   {headers:new Headers ({
        //     "Token": this.state.token,
        // })})
        //     .then(response => response.json())
        //     .then(data => this.setState({data}))
        //     .catch(e => {
        //         return e;
        //     });
       
      }

      callDetail(){
            
        fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrder?NO_TRA_MAS='+this.state.id,
        {headers:new Headers ({
          "Token": this.state.token,
      })}
        ) 
            .then(response => response.json())
            .then(data => this.setState({data}))
            .catch(error =>{
              console.log("aaa" + error)
            })
      }

      changeHandler=(e: any)=>{
        this.setState({[e.target.name]: e.target.value})
      }
      callApi (){
         
      }
      dataListDetail() {
        return(
          this.state.dataDetail.map((item: any, i: any)=>
                  <tr  key = {i} >
                      <th  scope="row">{item.ProductCode}</th>
                      <td >{item.Color}</td>
                      <td >{item.Size}</td>
                      <td >{item.Quantity}</td>
                      <td >{item.OrderPrice}</td>
                      <td >{item.PurchasePrice}</td>
                      <td >{item.Rate}</td>
                      <td >{item.Note}</td>
                  </tr>
              )
        );
  }
    render(){
        console.log("bbbbbb" + this.props.onChange)
        console.log( this.state.data);
        console.log("bbbbbb" + this.state.dataDetail)
        this.setState({id: this.props.onChange})
        return (
        <div className="modal" id="myModal"  role="dialog" aria-hidden="true">
        <div className="modal-dialog modal-full" role="document">
            <div className="modal-content">
                <div className="modal-header">
                    <h2 style={{width: "98%"}}>Chi tiết đơn hàng</h2>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close" style={{width: "2%"}}>
                        <span aria-hidden="true">×</span>
                    </button>
                </div>
                <div className="modal-body p-4" id="result">
                <form>
                         <div className="row">
                             <div className="col-sm-5"> 
                                 <div className="form-group">
                                     <label >Mã đơn hàng:</label>
                                     <input type="text" className="form-control"   />
                                 </div>  
                                 <div className="form-group">
                                     <label >Ngày:</label>
                                     <input type="text" className="form-control" value={this.state.data.OrderDate}/>
                                 </div>
                                 <div className="form-group">
                                     <label >Trạng thái:</label>
                                     <input type="text" className="form-control" value={this.state.data.Status}/>
                                 </div>
                             </div>
                             <div className="col-sm-2"></div>
                             <div className="col-sm-5">
                                 <div className="form-group">
                                     <label >Ghi chú:</label>
                                     <textarea className="form-control" rows={10} id="comment" value={this.state.data.Note}></textarea>
                                 </div>
                             </div>
                         </div>
                     </form>
                     <div className="line"></div>
                     <table className="table table-bordered table-striped">
                         <thead>
                             <tr>
                             <th scope="col">Mã trạng thái</th>
                             <th scope="col">Màu</th>
                             <th scope="col">Size</th>
                             <th scope="col">Số lượng</th>
                             <th scope="col">Giá đơn hàng</th>
                             <th scope="col">Giá mua</th>
                             <th scope="col">Tỷ lệ</th>
                             <th scope="col">Ghi chú</th>
                             </tr>
                         </thead>
                         <tbody className="height-tbody" >
                            {/* {this.dataListDetail()} */}
                         </tbody>
                     </table>
                     

                </div>
                <div className="modal-footer">
                    {/* <button type="button" className="btn btn-secondary" data-dismiss="modal">OK</button> */}
                    <div className="row">
                         <div className="col-sm-1">
                             {/* <button type="submit" className="btn btn-primary">Hủy</button> */}
                         </div>
                         <div className="col-sm-11">
                             <button type="submit" className="btn btn-primary" data-dismiss="modal">Quay lại</button>
                         </div>
                     </div>
                </div>
            </div>
        </div>
    </div>
    
        );
    }

}

export default DetailOrder;
