import React from 'react';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

class Sidebar extends React.Component<any, any> {
    constructor(props: any) {
        super(props);
        this.state = {
            showAdmin: false,
            showCustomer: false
        }
      }
      componentWillMount(){
        let num1 : any
        num1  = 1
        let num2 : any
        num2  = 8
        if( sessionStorage.getItem('ID_OTY') == num2 ){
            this.setState({showAdmin: false})
            this.setState({showCustomer: true})
            }
            else if(sessionStorage.getItem('ID_OTY') == num1){
                this.setState({showCustomer: true})
                this.setState({showAdmin: true})
        }
    }
    render(){
        const styleshow2 = {
            display: this.state.showAdmin ?"block" : "none"
		}
		const styleshow1 = {
            display: this.state.showCustomer ?"block" : "none"
		}
        return (
            <nav id="sidebar">
                <div className="sidebar-header">
                    <h3>O-NOLIMIT</h3>
                    <strong>ON</strong>
                </div>

                <ul  className="list-unstyled components">
                    <Link  to="/home" className="active">
                           <i className="glyphicon glyphicon-home"></i>
                            Trang chủ  
                    </Link>
                   
                    <li>
                        <a style={styleshow1}  href="#orderSubmenu" data-toggle="collapse" aria-expanded="false">
                            <i className="glyphicon glyphicon-duplicate"></i>
                                Đơn hàng
                        </a>
                        <ul style={styleshow1}  className="collapse list-unstyled" id="orderSubmenu">
                            <Link to="order">Danh mục đơn hàng</Link>
                            <Link to="detail-order">Chi tiết đơn hàng</Link>
                            <Link  to="add-order">Thêm đơn hàng</Link>
                        </ul>
                        <div style={styleshow2}>
                            <a href="#homeSubmenu" data-toggle="collapse" aria-expanded="false">
                                <i className="glyphicon glyphicon-user"></i>
                                    Khách hàng
                            </a>
                            <ul className="collapse list-unstyled" id="homeSubmenu">
                                <Link to="customer">Danh mục khách hàng</Link>
                                <Link to="group-customer">Nhóm khách hàng</Link>
                                <Link  to="debt-customer">Công nợ khách hàng</Link>
                            </ul>
                            <a href="#pageSubmenu" data-toggle="collapse" aria-expanded="false">
                                <i className="glyphicon glyphicon-user"></i>
                                Nhân viên
                            </a>
                            <ul className="collapse list-unstyled" id="pageSubmenu">
                                <Link to="list-employees">Danh mục nhân viên</Link>
                                <Link to="add-employees">Thêm mới nhân viên</Link>
                            </ul>
                        </div>
                       
                    </li>
                    
                    <Link to="setting">
                            <i className="glyphicon glyphicon-cog"></i>
                            Thiết lập
                    </Link>
                </ul>
            </nav>
        );
    }

}

export default Sidebar;
