import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './SignUp.css';

class SignUp extends React.Component<any, any> {
    constructor(props: any) {
        super(props);
    
        this.state = {
            TEXT6: '',
            TEXT11: '',
            NAME_OBJ0: '',
            DATE2: '',
            TEXT2: '',
            TEXT4: '',
          signUpEmail: '',
          signUpPassword: '',
        };
    }
    onSignUp() {
        // Grab state
        const {
          signUpEmail,
          signUpPassword,
        } = this.state;
    
        this.setState({
          isLoading: true,
        });
    
        // Post request to backend
        fetch('/api/account/signup', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            email: signUpEmail,
            password: signUpPassword,
          }),
        }).then(res => res.json())
          .then(json => {
            console.log('json', json);
            if (json.success) {
              this.setState({
                signUpError: json.message,
                isLoading: false,
                signUpEmail: '',
                signUpPassword: '',
              });
            } else {
              this.setState({
                signUpError: json.message,
                isLoading: false,
              });
            }
          });
      }
    
    componentDidMount() {
        // fetch('http://www.o-nolimit.com/Admin/Api/Register/SignUp')
        //     .then(response => response.json())
        //     .then(data => this.setState({ data }))
        //     .catch(e => {
        //         console.log(e);
        //         return e;
        //     });
        //     // this.handleSubmit;
      }
    render(){
        return (
            <form action="/action_page.php">
                <div className="container signin">
                    <h1>Register</h1>
                    <p>Please fill in this form to create an account.</p>
                    <hr/>

                    <label ><b>Mail đăng ký</b></label>
                    <input type="text" placeholder="Nhập mail đăng ký" name="email" required/>

                    <label><b>Mật khẩu</b></label>
                    <input type="password" placeholder="Nhập mật khẩu" name="psw" required/>

                    <label ><b>Xác nhận mật khẩu</b></label>
                    <input type="password" placeholder="Xác nhận mật khẩu" name="psw-repeat" required/>
                    <hr/>
                    <h3>
                    <b>Thông tin liên lạc</b>
                    </h3>
                    
                    <br/>
                    <label ><b>Họ và tên</b></label>
                    <input type="text" placeholder="Nhập họ và tên" name="name" required/>
                    <br/>
                    <label ><b>Ngày tháng năm sinh</b></label><br/>
                    <input className="date-style " type="date" name="bday"></input>
                    <br/>
                    <label ><b>Số điện thoại</b></label>
                    <input type="text" placeholder="Nhập số điện thoại" name="phone-number" required/>
                    <br/>
                    <label ><b>Địa chỉ</b></label>
                    <input type="text" placeholder="Nhập địa chỉ" name="location" required/>
                    <br/>
                    <label ><b>Mail</b></label>
                    <input type="text" placeholder="Nhập mail" name="mail" required/>
                    <br/>
                    <label ><b>Facebook</b></label>
                    <input type="text" placeholder="Nhập tên Facebook" name="user-fb" required/>
                    <br/>
                    <label ><b>Mã kho khách</b></label><br/>
                    <label className="radio-inline"><input type="radio" name="optradio" checked/>Kho A</label>
                    <label className="radio-inline"><input type="radio" name="optradio"/>Kho B</label>
                    <label className="radio-inline"><input type="radio" name="optradio"/>Kho C</label>
                    <hr/>
                    <p>By creating an account you agree to our <a href="#">Terms & Privacy</a>.</p>

                    <button type="submit" className="registerbtn">Register</button>
                </div>
                
                <div className="container signin-text ">
                    <p>Already have an account? <a href="#">Sign in</a>.</p>
                </div>
            </form>
            
           
        );
    }

}

export default SignUp;
