import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';
import axios from 'axios';
import {
    BrowserRouter as Router,
    useParams,
    useLocation
  } from "react-router-dom";

class AddOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {

           //mã nv
           EmployeeCode: "",
           //ten nv
           EmployeeName: "",
           //dia chi
           Address: "" ,
           //mail
           Email: "",
           //
           HomePhone: "",
           //
           OfficePhone: "",
           //web
           MobilePhone: "",
            //mail
            Fax: "",
            //
            Birthday: "",
            //
            Sex: "",
            //
            UserName: "",
            //
            Password: ""
        };
        this.bodyTable= this.bodyTable.bind(this)
      }

      submitHandler = (e: any) =>{
        e.preventDefault()
        console.log(this.state)
        axios
        .post('http://api.o-nolimit.com/Admin/Api/Employee/InsertEmployeeApi', this.state)
        .then(response =>{
          console.log(response)
        })
        .catch(error =>{
          console.log(error)
        })
      }
      changeHandler=(e: any)=>{
        this.setState({[e.target.name]: e.target.value})
      }
       useQuery() {
        return new URLSearchParams(useLocation().search);
      }
    

    render(){
        const {
            //mã nv
            EmployeeCode,
            //ten nv
            EmployeeName,
            //dia chi
            Address ,
            //mail
            Email,
            //
            HomePhone,
            //
            OfficePhone,
            //web
            MobilePhone,
             //mail
             Fax,
             //
             Birthday,
             //
             Sex,
             //
             UserName,
             //
             Password
         } = this.state;
        return (
            <div className="wrapper">
           <Sidebar/>

            <div id="content">
               <Header/>
               <div style={{padding: "20px"}}>
                    <h2>Chức năng thao tác thêm mới đơn hàng</h2>
                    <div className="line"></div>
                    <form action="/action_page.php" onSubmit={this.submitHandler}>
                    <div className="row">
                             <div className="col-sm-5"> 
                                 <div className="form-group">
                                     <label >Mã đơn hàng:</label>
                                     <input type="text" className="form-control"   />
                                 </div>  
                                 <div className="form-group">
                                     <label >Ngày:</label>
                                     <input type="text" className="form-control" />
                                 </div>
                                 <div className="form-group">
                                     <label >Trạng thái:</label>
                                     <input type="text" className="form-control" />
                                 </div>
                             </div>
                             <div className="col-sm-2"></div>
                             <div className="col-sm-5">
                                 <div className="form-group">
                                     <label >Ghi chú:</label>
                                     <textarea className="form-control" rows={10} id="comment"></textarea>
                                 </div>
                             </div>
                         </div>
                   
                     <div className="line"></div>
                     <table className="table table-bordered table-striped">
                         <thead>
                             <tr>
                             <th scope="col">Mã trạng thái</th>
                             <th scope="col">Màu</th>
                             <th scope="col">Size</th>
                             <th scope="col">Số lượng</th>
                             <th scope="col">Giá đơn hàng</th>
                             <th scope="col">Giá mua</th>
                             <th scope="col">Tỷ lệ</th>
                             <th scope="col">Ghi chú</th>
                             </tr>
                         </thead>
                         <tbody className="height-tbody" >
                           
                         <tr>
                            <th scope="row">1</th>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>@mdo</td>
                        </tr>
                         </tbody>
                     </table>
                        <div className="row">
                            <div className="col-sm-10"></div>
                            <div className="col-sm-1">
                                <button type="submit" className="btn btn-primary">Hủy</button>
                            </div>
                            <div className="col-sm-1">
                                <button type="submit" className="btn btn-primary">Lưu</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>
            <div className="footer">
                <p>Footer</p>
            </div>
        </div>
        );
    }

}

export default AddOrder;
