import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';

class AddOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: null
        };
      }

      componentDidMount() {
        // fetch('https://jsonplaceholder.typicode.com/todos/1')
        //     .then(response => response.json())
        //     .then(data => this.setState({ data }))
        //     .catch(e => {
        //         console.log(e);
        //         return e;
        //     });
      }

    render(){
        const {
            order: {
                OrderCode,
                
            },
            //mã nv
            EmployeeCode,
            //ten nv
            EmployeeName,
            //dia chi
            Address ,
            //mail
            Email,
            //
            HomePhone,
            //
            OfficePhone,
            //web
            MobilePhone,
             //mail
             Fax,
             //
             Birthday,
             //
             Sex,
             //
             UserName,
             //
             Password
         } = this.state;
        return (
            <div className="wrapper">
           <Sidebar/>

            <div id="content">
               <Header/>
               <div className= "scrol-body">
                    <h2>Thêm mới đơn hàng</h2>
                    <div className="line"></div>
                    <form>
                        <h3>Đơn hàng</h3>
                        <div className="row">
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >Mã đơn hàng:</label>
                                    <input type="text" className="form-control" placeholder="Nhập mã đơn hàng"/>
                                </div>  
                                <div className="form-group">
                                    <label >Ngày:</label>
                                    <input type="text" className="form-control" placeholder="Nhập ngày tạo đơn hàng"/>
                                </div>
                                <div className="form-group">
                                    <label >Trạng thái:</label>
                                    <input type="text" className="form-control" placeholder="Nhập trạng thái"/>
                                </div>
                            </div>
                            <div className="col-sm-2"></div>
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >Ghi chú:</label>
                                    <textarea className="form-control" rows={10} id="comment"></textarea>
                                </div>
                            </div>
                        </div>
                        <div className="line"></div>
                        <h3>Chi tiết đơn hàng</h3>
                        <div className="row">
                            <div className="col-sm-5">
                            <div className="line"></div>
                                <div className="form-group">
                                    <label >Mã trạng thái:</label>
                                    <input type="text" className="form-control" placeholder="Nhập mã trạng thái"/>
                                </div>  
                                <div className="form-group">
                                    <label >Màu:</label>
                                    <input type="text" className="form-control" placeholder="Nhập màu"/>
                                </div>
                                <div className="form-group">
                                    <label >Size:</label>
                                    <input type="text" className="form-control" placeholder="Nhập size"/>
                                </div>
                                <div className="form-group">
                                    <label >Số lượng:</label>
                                    <input type="text" className="form-control" placeholder="Nhập số lượng"/>
                                </div>
                            </div>
                            <div className="col-sm-2"></div>
                            <div className="col-sm-5">
                                <div className="form-group">
                                    <label >Giá đơn hàng:</label>
                                    <input type="text" className="form-control" placeholder="Nhập giá đơn hàng"/>
                                </div>  
                                <div className="form-group">
                                    <label >Giá mua:</label>
                                    <input type="text" className="form-control" placeholder="Nhập giá mua"/>
                                </div>
                                <div className="form-group">
                                    <label >Tỷ lệ:</label>
                                    <input type="text" className="form-control" placeholder="Nhập tỷ lệ"/>
                                </div>
                            </div>
                        </div>
                        <div className="row">
                            <div className="col-sm-10"></div>
                            <div className="col-sm-1">
                                <button type="submit" className="btn btn-primary">Hủy</button>
                            </div>
                            <div className="col-sm-1">
                                <button type="submit" className="btn btn-primary">Lưu</button>
                            </div>
                        </div>
                    </form>
                </div>
            </div>

        </div>
        );
    }

}

export default AddOrder;
