import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import { any } from 'prop-types';
import queryString from 'query-string';
import axios from 'axios';
import './order.css';

class DetailOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {

                lstDetail: [
                    {
                        OrderId: 0,
                        OrderDetailId: 0,
                        Link: "",
                        ProductCode: "",
                        Color: "",
                        Size: "",
                        Quantity: any,
                        OrderPrice: any,
                        PurchasePrice: any,
                        Rate: any,
                        Note: ""
                    },
                ],
                order: {
                    OrderId: 0,
                    OrderCode: "",
                    UserId: 0,
                    OrderDate: "",
                    Note: "",
                    VISIBLE: true,
                    Status: ""
                },
          token: sessionStorage.getItem('Token'),
          showEdit: false,
          userid: sessionStorage.getItem('UserId')
        };
        
      }
         
      componentDidMount() {
        const values = queryString.parse(this.props.location.search);
        fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrderMaster?NO_TRA_MAS='+ values.OrderId,
        {headers:new Headers ({
            "Token": this.state.token,
        })}
        )
            .then(response => response.json())
            .then(order => this.setState({order}))
            .catch(e => {
                console.log(e);
                return e;
            });

            fetch('http://api.o-nolimit.com/Admin/Api/Order/GetOrderDetail?NO_TRA_MAS='+ values.OrderId,
        {headers:new Headers ({
            "Token": this.state.token,
        })}
        )
            .then(response => response.json())
            .then(lstDetail => this.setState({lstDetail}))
            .catch(e => {
                console.log(e);
                return e;
            });
      }

      changeHandler=(e: any)=>{ 
        let dataOrder = this.state.data.order;
        dataOrder[e.target.name] = e.target.value;
        this.setState(this.state.order);
        console.log("order")
      }
      changeHandlerDetail=(e: any)=>{ 
        let dataLstDetail = this.state.data.lstDetail;
        dataLstDetail[e.target.name] = e.target.value;
        this.setState(this.state.lstDetail);
        console.log("detail")
      }

      pushTable(){
          var row = {
                        "OrderId": 0,
                        "OrderDetailId": 0,
                        "Link": "",
                        "ProductCode": "",
                        "Color": "",
                        "Size": "",
                        "Quantity": any,
                        "OrderPrice": any,
                        "PurchasePrice": any,
                        "Rate": any,
                        "Note": ""
          }
          this.state.lstDetail.push(row);
          this.setState(this.state.lstDetail);
      }


      submitHandler = (e: any) =>{
        let dataOrder = this.state.data.order;
        dataOrder.UserId=this.state.userid;
        e.preventDefault();
        //console.log(this.state.data);
        const values = queryString.parse(this.props.location.search);
        const headers = {
            'Content-Type': 'application/json',
            'Token': this.state.token
          }
            axios.put('http://api.o-nolimit.com/Admin/Api/Order/UpdateOrder', this.state,
            {
                headers : headers
            }).then(response =>{
              if(response.status==200)
              {
                  alert("Chỉnh sửa thông tin đơn hàng thành công!");
                  window.location.href= window.location.href ;
              }
              else  
              {
                alert(response.statusText);
              }
            })
            .catch(error =>{
              alert(error);
            });

      }
      changes(i: any){
        return(
            <div className="modal" id="myModal123" role="dialog" aria-labelledby="myExtraLargeModalLabel" aria-hidden="true">
            <div className="modal-dialog modal-xl modal-dialog-centered">
                <div className="modal-content">
                    <div className="modal-header">
                    <h4 className="modal-title">Chỉnh sửa đơn hàng</h4>
                    <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                    </button>
                    </div>
                    <div className="modal-body">
                    
                        <div style={{ border:"0px",  width:"100%" ,height:"470px" }}>
                        <form>
                       <div className="row">
                            <div className="col-sm-5">
                                <label ><b>Mã trạng thái</b></label>
                                <input 
                                type="text" 
                                name="ProductCode" 
                                required value={this.state.lstDetail[i].ProductCode} 
                                onChange={this.changeHandler}/>
                                <br/>
                                <label ><b>Màu</b></label>
                                <input 
                                type="text" 
                                name="Phone" 
                                // required value={Phone} 
                                onChange={this.changeHandler}/>
                                <br/>
                                <label ><b>Size</b></label>
                                <input 
                                type="text" 
                                name="Phone" 
                                // required value={Phone} 
                                onChange={this.changeHandler}/>
                                <br/>
                                <label ><b>Số lượng</b></label>
                                <input 
                                type="text" 
                                name="Phone" 
                                // required value={Phone} 
                                onChange={this.changeHandler}/>
                            </div>
                            <div className="col-sm-2"></div>
                            <div className="col-sm-5">
                                <label ><b>Giá đơn hàng</b></label>
                                <input 
                                type="text" 
                                name="Phone" 
                                // required value={Phone} 
                                onChange={this.changeHandler}/>
                                <br/>
                                <label ><b>Giá mua</b></label>
                                <input 
                                type="text" 
                                name="Phone" 
                                // required value={Phone} 
                                onChange={this.changeHandler}/>
                                <br/>
                                <label ><b>Tỷ lệ</b></label>
                                <input 
                                type="text" 
                                name="Phone" 
                                // required value={Phone} 
                                onChange={this.changeHandler}/>
                            </div>
                       </div>
                   </form>
                        </div>
                    </div>
                    <div className="modal-footer">
                        <div style={ { margin: "auto", width: "100px", height:"100px" } } >
                            <button type="button" className="btn btn-danger" data-dismiss="modal">Đóng</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        )
      }
    render(){
        const {
            OrderId,
            OrderCode,
            UserId ,
            OrderDate ,
            Note ,
            VISIBLE ,
            Status 
         } =  this.state.order;
         const {
            OrderDetailId,
            Link,
            ProductCode ,
            Color ,
            Size ,
            Quantity ,
            OrderPrice ,
            PurchasePrice,
            Rate, 
         } =  this.state.lstDetail;
        return (
            <div>
                <div className="modal-body p-4" id="result">
                   
                         <div className="row">
                             <div className="col-sm-5"> 
                                 <div className="form-group">
                                     <label >Mã đơn hàng:</label>
                                     <input type="text" className="form-control"  
                                        name="OrderCode" 
                                        required value={OrderCode} 
                                        onChange={this.changeHandler} />
                                 </div>  
                                 <div className="form-group">
                                     <label >Ngày:</label>
                                     <input type="date" className="form-control"
                                        name="OrderDate" 
                                        required value={OrderDate} 
                                        onChange={this.changeHandler}
                                    />
                                 </div>
                                 {/* <div className="form-group">
                                     <label >Trạng thái:</label>
                                     <input type="text" className="form-control" 
                                        name="Status" 
                                        required value={Status} 
                                        onChange={this.changeHandler}
                                     />
                                 </div> */}
                             </div>
                             <div className="col-sm-2"></div>
                             <div className="col-sm-5">
                                 <div className="form-group">
                                     <label >Ghi chú:</label>
                                     <textarea className="form-control" rows={10} id="comment" 
                                        name="Note" 
                                        required value={Note} 
                                        onChange={this.changeHandler}
                                     ></textarea>
                                 </div>
                             </div>
                         </div>
                     
                     <div className="line"></div>
                     <table className="table table-bordered table-striped">
                         <thead>
                             <tr>
                             <th scope="col">Mã trạng thái</th>
                             <th scope="col">Màu</th>
                             <th scope="col">Size</th>
                             <th scope="col">Số lượng</th>
                             <th scope="col">Giá đơn hàng</th>
                             <th scope="col">Giá mua</th>
                             <th scope="col">Tỷ lệ</th>
                             <th scope="col">Chức năng</th>
                             </tr>
                         </thead>
                         <tbody className="height-tbody" >
                            {
                                 this.state.lstDetail.map((item: any, i: any)=>
                                 <tr  key = {i} id="styleInput">

                                     <td >{item.ProductCode}</td>

                                     <td >{item.Color}</td>

                                     <td >{item.Size}</td>

                                     <td >{item.Quantity}</td>

                                     <td >{item.OrderPrice}</td>

                                     <td >{item.PurchasePrice}</td>

                                     <td >{item.Rate}</td>
                                      <td >
                                      <button type="submit" className="btn btn-primary" data-toggle="modal" data-target="#myModal123" 
                                        onClick={()=>{this.changes(i)}}
                                      >Sửa</button>
                                      </td>
                                 </tr>
                             )
                            }
                         </tbody>
                     </table>
                     <div className="line"></div>
                     <div className="row">
                         <div className="col-sm-1">
                         <button  onClick={() => {this.pushTable()}} type="button" className="btn btn-primary" data-dismiss="modal">+</button>
                         </div>
                         <div className="col-sm-11">
                             <button type="submit" className="btn btn-primary" data-dismiss="modal">Lưu</button>
                         </div>
                     </div>

                </div>
                <div className="modal-footer">
                       {this.changes}
                       </div>

            </div>
        );
    }

}

export default DetailOrder;
