import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../home/Home.css';
import Sidebar from '../sidebar/sidebar';
import Header from '../sidebar/header';

class AddOrder extends React.Component<any, any> {
    constructor(props:any) {
        super(props);
        this.state = {
          data: null
        };
      }

      componentDidMount() {

      }

    render(){
        // const {
        //     order: {
        //         OrderCode,
                
        //     },
        //     //mã nv
        //     EmployeeCode,
        //     //ten nv
        //     EmployeeName,
        //     //dia chi
        //     Address ,
        //     //mail
        //     Email,
        //     //
        //     HomePhone,
        //     //
        //     OfficePhone,
        //     //web
        //     MobilePhone,
        //      //mail
        //      Fax,
        //      //
        //      Birthday,
        //      //
        //      Sex,
        //      //
        //      UserName,
        //      //
        //      Password
        //  } = this.state;
        return (
            <div className="modal" id="myModalAdd"  role="dialog" aria-hidden="true">
            <div className="modal-dialog modal-full" role="document">
                <div className="modal-content">
                    <div className="modal-header">
                        <h2 style={{width: "98%"}}>Chi tiết đơn hàng</h2>
                        <button type="button" className="close" data-dismiss="modal" aria-label="Close" style={{width: "2%"}}>
                            <span aria-hidden="true">×</span>
                        </button>
                    </div>
                    <div className="modal-body p-4" id="result">
                    <form>
                             <div className="row">
                                 <div className="col-sm-5"> 
                                     <div className="form-group">
                                         <label >Mã đơn hàng:</label>
                                         <input type="text" className="form-control"  />
                                     </div>  
                                     <div className="form-group">
                                         <label >Ngày:</label>
                                         <input type="text" className="form-control" />
                                     </div>
                                     <div className="form-group">
                                         <label >Trang thái:</label>
                                         <input type="text" className="form-control" />
                                     </div>
                                 </div>
                                 <div className="col-sm-2"></div>
                                 <div className="col-sm-5">
                                     <div className="form-group">
                                         <label >Ghi chú:</label>
                                         <textarea className="form-control" rows={10} id="comment" ></textarea>
                                     </div>
                                 </div>
                             </div>
                         </form>
                         <div className="line"></div>
                         <table className="table table-bordered table-striped">
                             <thead>
                                 <tr>
                                 <th scope="col">Mã trạng thái</th>
                                 <th scope="col">Màu</th>
                                 <th scope="col">Size</th>
                                 <th scope="col">Số lượng</th>
                                 <th scope="col">Giá đơn hàng</th>
                                 <th scope="col">Giá mua</th>
                                 <th scope="col">Tỷ lệ</th>
                                 <th scope="col">Ghi chú</th>
                                 </tr>
                             </thead>
                             <tbody className="height-tbody" >
                            
                             </tbody>
                         </table>
                         
    
                    </div>
                    <div className="modal-footer">
                        {/* <button type="button" className="btn btn-secondary" data-dismiss="modal">OK</button> */}
                        <div className="row">
                             <div className="col-sm-6">
                                 <button type="submit" className="btn btn-primary">Hủy</button>
                             </div>
                             <div className="col-sm-6">
                                 <button type="submit" className="btn btn-primary">Lưu</button>
                             </div>
                         </div>
                    </div>
                </div>
            </div>
        </div>
        );
    }

}

export default AddOrder;
